function onCreate()makeLuaSprite('justimage',nil,0.22,0.22)makeLuaSprite('rgbcon',nil,0.006,-0.006)endfunction onUpdate()setShaderFloat('111','dark',getProperty('justimage.x'))setShaderFloat('shaderImage','dark',getProperty('justimage.y'))if curStep == 416 thendoTweenX('jjjj','justimage',0,0.01)doTweenY('xxxxx','justimage',0,0.01)endif curStep == 928 thendoTweenX('jjj','justimage',0.22,3)doTweenY('xxxx','justimage',0.22,3)endif curStep == 1216 thendoTweenX('jjjj','justimage',0,0.01)doTweenY('xxxxx','justimage',0,0.01)endend