function opponentNoteHit()health = getProperty('health')if getProperty('dad.curCharacter') == 'bf-spirit-opponent' and getProperty('health') > 0.2 or getProperty('dad.curCharacter') == 'bf-spirit-opponent-alt' and getProperty('health') > 0.2 or getProperty('dad.curCharacter') == 'bf-spirit-mad' and getProperty('health') > 0.2  or getProperty('dad.curCharacter') == 'bf-spirit-smug' and getProperty('health') > 0.2 thensetProperty('health', health- 0.015);endend