function onStepHit()if curBeat == 320 thenremoveLuaSprite('sky');	removeLuaSprite('front');removeLuaSprite('light1');removeLuaSprite('light2');removeLuaSprite('light3');removeLuaSprite('light4');removeLuaSprite('frontCar');removeLuaSprite('backCar');removeLuaSprite('filter');makeLuaSprite('sky','limo3/time-remix/phase2/limoSky',-3200,-800)setScrollFactor('sky',0.7,0.7)addLuaSprite('sky')makeAnimatedLuaSprite('backMan','limo3/time-remix/backMan',-400,-100)addAnimationByPrefix('backMan','idle','backMan',24,false)addLuaSprite('backMan')makeLuaSprite('front','limo3/time-remix/frontStreet',300,-200)setScrollFactor('front',0.9,0.9)screenCenter('front','x')addLuaSprite('front')makeLuaSprite('light1','limo3/time-remix/streetLight',-500,-600)addLuaSprite('light1')makeLuaSprite('light2','limo3/time-remix/streetLight',300,-600)addLuaSprite('light2')makeLuaSprite('light3','limo3/time-remix/streetLight',1100,-600)addLuaSprite('light3')makeLuaSprite('light4','limo3/time-remix/streetLight',1900,-600)addLuaSprite('light4')makeLuaSprite('frontCar','limo3/time-remix/phase2/frontCar',-25,0)addLuaSprite('frontCar')makeAnimatedLuaSprite('frontMan','limo3/time-remix/frontMans',450,100)addAnimationByPrefix('frontMan','idle','frontMans',24,false)setScrollFactor('frontMan',1.8,1.8)addLuaSprite('frontMan',true)makeAnimatedLuaSprite('fog','limo3/time-remix/funkerFog',-600,-600)addAnimationByPrefix('fog','fog','fog idle',24,true)setGraphicSize('fog',3000,3000)screenCenter('fog')setScrollFactor('fog',0,0)addLuaSprite('fog',true)makeLuaSprite('filter','limo3/time-remix/phase2/filter',-3200,-800)setScrollFactor('filter',0.7,0.7)setProperty('filter.alpha',1)addLuaSprite('filter',true)endend