function onStepHit()if curBeat == 68 thenremoveLuaSprite('sky');	removeLuaSprite('front');removeLuaSprite('light1');removeLuaSprite('light2');removeLuaSprite('light3');removeLuaSprite('light4');removeLuaSprite('frontCar');removeLuaSprite('backCar');removeLuaSprite('filter');makeLuaSprite('sky','stage_assets/limo/defeat/limoSky',-3200,-800)setScrollFactor('sky',0.7,0.7)addLuaSprite('sky')makeLuaSprite('sky2', 'stage_assets/limo/defeat/lol thing', -1015, 15);scaleObject('sky2', 1.47, 1.47)setProperty('sky2.antialiasing', getPropertyFromClass('ClientPrefs', 'globalAntialiasing'))addLuaSprite('sky2');makeLuaSprite('front','stage_assets/limo/defeat/frontStreet',300,-200)setScrollFactor('front',0.9,0.9)screenCenter('front','x')addLuaSprite('front')makeLuaSprite('iluminao omaga', 'stage_assets/limo/defeat/iluminao omaga', -545, 125);setProperty('iluminao omaga.antialiasing', getPropertyFromClass('ClientPrefs', 'globalAntialiasing'))addLuaSprite('iluminao omaga');-- scaleObject('iluminao omaga', 1.2, 1.2)setBlendMode('iluminao omaga', 'add')makeLuaSprite('frontCar','stage_assets/limo/defeat/frontCar',-25,0)addLuaSprite('frontCar')makeLuaSprite('deadBG', 'stage_assets/limo/defeat/deadBG', -800, 425);scaleObject('deadBG', 0.454, 0.454)setProperty('deadBG.antialiasing', getPropertyFromClass('ClientPrefs', 'globalAntialiasing'))addLuaSprite('deadBG')makeLuaSprite('deadFG', 'stage_assets/limo/defeat/deadFG', -715, 695);scaleObject('deadFG', 0.445, 0.445)setProperty('deadFG.antialiasing', getPropertyFromClass('ClientPrefs', 'globalAntialiasing'))addLuaSprite('deadFG')endend