function onStepHit()if curBeat == 292 thenremoveLuaSprite('sky');	removeLuaSprite('sky2')removeLuaSprite('front');removeLuaSprite('frontCar');removeLuaSprite('iluminao omaga');removeLuaSprite('deadBG');removeLuaSprite('deadFG');makeLuaSprite('sky','stage_assets/limo/limoSky',-3200,-800)setScrollFactor('sky',0.7,0.7)addLuaSprite('sky')makeLuaSprite('front','stage_assets/limo/frontStreet',300,-200)setScrollFactor('front',0.9,0.9)screenCenter('front','x')addLuaSprite('front')makeLuaSprite('light1','stage_assets/limo/streetLight',-500,-600)addLuaSprite('light1')makeLuaSprite('light2','stage_assets/limo/streetLight',300,-600)addLuaSprite('light2')makeLuaSprite('light3','stage_assets/limo/streetLight',1100,-600)addLuaSprite('light3')makeLuaSprite('light4','stage_assets/limo/streetLight',1900,-600)addLuaSprite('light4')makeLuaSprite('backCar','stage_assets/limo/backCar',-300,50)scaleObject('backCar',1.2,1)addLuaSprite('backCar')makeLuaSprite('frontCar','stage_assets/limo/frontCar',-25,0)addLuaSprite('frontCar')makeLuaSprite('filter','stage_assets/limo/filter',-3200,-800)setScrollFactor('filter',0.7,0.7)setProperty('filter.alpha',1)addLuaSprite('filter',true)makeLuaSprite('lay','overlay',0,0)setObjectCamera('lay','hud')addLuaSprite('lay')endend