function onCreate()makeLuaSprite('black','',-100,-100)makeGraphic('black',2000,2000,'000000')setObjectCamera('black','other')addLuaSprite('black')endfunction onUpdatePost()setObjectCamera('countdownSet','camOther')setObjectCamera('countdownReady','camOther')setObjectCamera('countdownGo','camOther')endfunction onCreatePost()setProperty('sky.alpha',0)setProperty('sky2.alpha',1)setProperty('camHUD.alpha',0)setProperty('lay2.alpha',1)setProperty('gf.visible',false)endfunction onStepHit()if curStep == 128 thendoTweenAlpha('black','black',0,5,'')endif curStep == 248 thendoTweenAlpha('lay2','lay2',0,0.8,'')doTweenAlpha('camHUD','camHUD',1,0.8,'')endif curStep == 1536 thendoTweenAlpha('lay2','lay2',1,1,'')doTweenAlpha('camHUD','camHUD',0,1,'')endif curStep == 1632 thendoTweenAlpha('black','black',1,5,'')endend