function onCreate()makeLuaSprite('lights', 'stage_assets/father/lights', -850, -400);addLuaSprite('lights', true);scaleObject('lights', 1.1, 1.2);setScrollFactor('lights', 0.9, 0.9);setProperty('lights.visible', true);makeAnimatedLuaSprite('tempestDudes', 'stage_assets/fatherTempest/tempestDudes', -600, 800);addAnimationByPrefix('tempestDudes','animation','fgCrowd',24,false);setScrollFactor('tempestDudes', 1, 1);scaleObject('tempestDudes', 2.1, 2.1);addLuaSprite('tempestDudes', true);setProperty('tempestDudes.visible', true);endfunction onBeatHit()if curBeat % 1 == 0 thenplayAnim('tempestDudes', 'animation', true, false, 0)endend