function onCreate()makeLuaSprite('black','',-100,-100)makeGraphic('black',2000,2000,'000000')setObjectCamera('black','other')addLuaSprite('black')endfunction onUpdatePost()setObjectCamera('countdownSet','camOther')setObjectCamera('countdownReady','camOther')setObjectCamera('countdownGo','camOther')endfunction onCreatePost()setProperty('camHUD.alpha',0)setProperty('lay2.alpha',1)endfunction onStepHit()if curStep == 16 thendoTweenAlpha('black','black',0,0.5,'')endif curStep == 8 thendoTweenAlpha('lay2','lay2',0,0.8,'')doTweenAlpha('camHUD','camHUD',1,0.5,'')endif curStep == 2096 thendoTweenAlpha('black','black',1,5,'')endend