function onStepHit()if curBeat == 64 thenremoveLuaSprite('sky');	removeLuaSprite('front');removeLuaSprite('light1');removeLuaSprite('light2');removeLuaSprite('light3');removeLuaSprite('light4');removeLuaSprite('frontCar');removeLuaSprite('backCar');removeLuaSprite('filter');precacheImage('stage_assets/limo3/time-remix/limoSky')precacheImage('stage_assets/limo3/time-remix/backMan')precacheImage('stage_assets/limo3/time-remix/frontStreet')precacheImage('stage_assets/limo3/time-remix/streetLight')precacheImage('stage_assets/limo3/time-remix/frontCar')precacheImage('stage_assets/limo3/time-remix/frontMans')precacheImage('stage_assets/limo3/time-remix/funkerFog')precacheImage('stage_assets/limo3/time-remix/filter')makeLuaSprite('sky','stage_assets/limo3/time-remix/limoSky',-3200,-800)setScrollFactor('sky',0.7,0.7)addLuaSprite('sky')makeAnimatedLuaSprite('backMan','stage_assets/limo3/time-remix/backMan',-400,-100)addAnimationByPrefix('backMan','idle','backMan',24,false)addLuaSprite('backMan')makeLuaSprite('front','stage_assets/limo3/time-remix/frontStreet',300,-200)setScrollFactor('front',0.9,0.9)screenCenter('front','x')addLuaSprite('front')makeLuaSprite('light1','stage_assets/limo3/time-remix/streetLight',-500,-600)addLuaSprite('light1')makeLuaSprite('light2','stage_assets/limo3/time-remix/streetLight',300,-600)addLuaSprite('light2')makeLuaSprite('light3','stage_assets/limo3/time-remix/streetLight',1100,-600)addLuaSprite('light3')makeLuaSprite('light4','stage_assets/limo3/time-remix/streetLight',1900,-600)addLuaSprite('light4')makeLuaSprite('frontCar','stage_assets/limo3/time-remix/frontCar',-25,0)addLuaSprite('frontCar')makeAnimatedLuaSprite('frontMan','stage_assets/limo3/time-remix/frontMans',450,100)addAnimationByPrefix('frontMan','idle','frontMans',24,false)setScrollFactor('frontMan',1.8,1.8)addLuaSprite('frontMan',true)makeAnimatedLuaSprite('fog','stage_assets/limo3/time-remix/funkerFog',-600,-600)addAnimationByPrefix('fog','fog','fog idle',24,true)setGraphicSize('fog',3000,3000)screenCenter('fog')setScrollFactor('fog',0,0)addLuaSprite('fog',true)makeLuaSprite('filter','stage_assets/limo3/time-remix/filter',-3200,-800)setScrollFactor('filter',0.7,0.7)setProperty('filter.alpha',1)addLuaSprite('filter',true)endend